// WithDeviceUseStatementNote adds a Note to the DeviceUseStatement.
func WithDeviceUseStatementNote(v Annotation) DeviceUseStatementOption {
	return func(r *DeviceUseStatement) {
		r.Note = append(r.Note, v)
	}
}
